svg.script:
xmlns="http://www.w3.org/2000/svg" var Doc; var O; var Col=new Array("red","cyan","green","yellow") var P=new Array() function startup(evt){ Root=document.documentElement Doc=document drawlines(100,10) coolabel(0,0,15,15) }
function drawlines(u,t){ B=Root.getBBox() var w=B.width var h=B.height for (i=0;i<=w/t;i++){ L=Doc.createElementNS(xmlns,"line") L.setAttribute("x1",i*t) L.setAttribute("x2",i*t) L.setAttribute("y1",0) L.setAttribute("y2",8) L.setAttribute("stroke","blue") L.setAttribute("stroke-width","1") Root.appendChild(L) if (i%10==0){ L=Doc.createElementNS(xmlns,"line") L.setAttribute("x1",i*t) L.setAttribute("x2",i*t) L.setAttribute("y1",0) L.setAttribute("y2",h) L.setAttribute("stroke","#08a") L.setAttribute("stroke-width","1") Root.appendChild(L) } } for (i=0;i<=h/t;i++){ L=Doc.createElementNS(xmlns,"line") L.setAttribute("x1",0) L.setAttribute("x2",8) L.setAttribute("y1",i*t) L.setAttribute("y2",i*t) L.setAttribute("stroke","blue") L.setAttribute("stroke-width","1") Root.appendChild(L) if (i%10==0){ L=Doc.createElementNS(xmlns,"line") L.setAttribute("x1",0) L.setAttribute("x2",w) L.setAttribute("y1",i*t) L.setAttribute("y2",i*t) L.setAttribute("stroke","#08a") L.setAttribute("stroke-width","1") Root.appendChild(L) } } }
function coolabel(x,y,ox,oy,l){ if (l==null) label=x+","+y else label=l E=Doc.createElementNS(xmlns,"circle") E.setAttribute("cx",x) E.setAttribute("cy",y) E.setAttribute("r",5) E.setAttribute("stroke","green") E.setAttribute("stroke-width","3") E.setAttribute("opacity",".4") E.setAttribute("fill","yellow") Root.appendChild(E) L=Doc.createElementNS(xmlns,"line") L.setAttribute("x1",x) L.setAttribute("x2",x+ox) L.setAttribute("y1",y) L.setAttribute("y2",y+oy) L.setAttribute("stroke","red") L.setAttribute("stroke-width","2") Root.appendChild(L) T=Doc.createElementNS(xmlns,"text") T.setAttribute("x",x+ox-label.length*2) if (oy>0) T.setAttribute("y",y+oy+12) else T.setAttribute("y",y+oy-4) T.setAttribute("font-size",12) if (l==null) T.setAttribute("fill","blue") else T.setAttribute("fill","black") msg=Doc.createTextNode(label) T.appendChild(msg) Root.appendChild(T)
} //
